void playScreenViewBase::handleTickEvent()
{
    frameCountXuLuLuoiInterval++;
    if(frameCountXuLuLuoiInterval == TICK_XULULUOI_INTERVAL)
    {
        //xuLuLuoi
        //When every N tick call virtual function
        //Call xuLyLuoi
        xuLyLuoi();
        frameCountXuLuLuoiInterval = 0;
    }
}
